import React, { Component } from "react";
class Items extends Component {
  state = {
    countries,
  };
  fetchData = async () => {
    let countries;
    await fetch("https://restcountries.eu/rest/v2/all").then((res) =>
      res.json().then((res) => (countries = res))
    );
    this.setState({
      countries,
    });
  };
  render() {
    this.fetchData();

    return (
      <div>
        {/* {countries && <Filters />}
        <CountryWrapper>
          {countries &&
            countries.map((el, index) => (
              <List_element country={el} id={index} key={index}>
                {el.name}
              </List_element>
            ))}
        </CountryWrapper> */}
      </div>
    );
  }
}
export default Items;
