import styled from "styled-components";
const Item = (props) => {
  return (
    <Wrapper>
      {/* <Image src={country.flag} alt={country.name}></Image>
      <StyledCountryName>{country.name}</StyledCountryName> */}
      <StyledCountryInfo>
        <li>{`Population: `}</li>
        <li>{`Region: `}</li>
        <li>{`Capital: `}</li>
      </StyledCountryInfo>
    </Wrapper>
  );
};
export default Item;
const Image = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-bottom: 2px solid ${(props) => props.theme.bg};
`;

// export const StyledLink = styled(Link)`
//   text-decoration: none;
// `;
const StyledCountryName = styled.h2`
  font-size: 1.5rem;
  color: ${(props) => props.theme.text};
  margin: 2rem;
`;
const StyledCountryInfo = styled.ul`
  /* margin: 2rem 2rem 5rem 2rem; */
  display: flex;
  flex-direction: column;
  color: ${(props) => props.theme.text};
  list-style-type: none;
  font-weight: 600;
  padding-left: 1rem;
  li {
    font-size: 1.1rem;
    margin-bottom: 0.5rem;
  }
`;

const Wrapper = styled.div`
  margin: 50px;
  min-width: 300px;
  // min-width: 280px;
  // max-width: 350px;
  height: 420px;
  background: ${(props) => props.theme.el_bg};
  box-shadow: 0 0 10px ${(props) => props.theme.shadow};
  border-radius: 8px;
  overflow: hidden;
  outline: none;
  cursor: pointer;
`;
