import React from "react";
import styled from "styled-components";
import { BsMoon } from "react-icons/bs";

class Header extends React.Component {
  render() {
    return (
      <StyledHeader>
        <Header__title>Where in the world?</Header__title>
        <Header__button onClick={this.props.toggleTheme}>
          <StyledMoonLogo size={17} />
          Dark Mode
        </Header__button>
      </StyledHeader>
    );
  }
}

export default Header;

const StyledHeader = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  grid-auto-flow: column;
  justify-items: start;
  width: 100%;
  min-width: 320px;
  background: ${(props) => props.theme.el_bg};
  border-bottom: 2px solid ${(props) => props.theme.bg};
  padding: 2rem;
`;
const StyledMoonLogo = styled(BsMoon)`
  color: ${(props) => props.theme.text};
`;

const Header__title = styled.h1`
  font-size: 1.5rem;
  font-weight: 800;
  text-align: center;
  margin-left: 2rem;
  color: ${(props) => props.theme.text};
`;

const Header__button = styled.button`
  margin-right: 2rem;
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: nowrap;
  background: ${(props) => props.theme.el_bg};
  color: ${(props) => props.theme.text};
  width: 100%;
  max-width: 9rem;
  border: 1px solid transparent;
  font-weight: 600;
  font-size: 1rem;
  outline: none;
  cursor: pointer;
  /* @media ${device.mobileM} {
    margin-right: 0;
  } */
`;
